Math.rot2d = function ( v, d, angle ) {

    v = v || { x:0, y:0 };
    var n = {};
    n.x = d * Math.cos( angle * Math.torad ) + v.x;
    n.y = d * Math.sin( angle * Math.torad ) + v.y;
    n.z = v.z;
    return n;

}

